  const health = useSelector((state) => state.auth.user.health)
  const enemyDamage = 5;
  const beginAttack = () => {
      const body = {
        userID,
        health,
        enemyDamage
      }
  }


  return (
    <div>
      <Navigation />
      <StatsTable/>
      battle arena
      <button onClick={beginAttack()}>Attack!</button>
    </div>
  );
};

