import * as types from "../types";
import axios from "axios";


export const updateEnemyHealth = ({enemyID, health, damage }) => async (dispatch) => {
    const body = {
        enemyID,
        enemyHealth,
        damage,
    };
        console.log(body, 'atck body')
    await axios
      .put(
        `http://localhost:9000/enemies/update/health/${body.enemyID}`,
        body
      )
      .then((res) => {
        dispatch({
          type: types.UPDATE_ENEMY_HEALTH,
             payload: res.data,
        });
      })
      .catch((err) => {
            console.log(err);
      });
  };
