import { useRouter } from "next/router";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import  {attack, spawnEnemy}  from "../../store/Actions/userActions";

import Navigation from "../../components/navigation/Navigation";
import StatsTable from '../../components/statsTable/StatsTable';
const Arena = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const userId = useSelector((state) => state.auth?.user?.id)
  const health = useSelector((state) => state.auth?.user?.health)
  const isSpawned = useSelector((state) => state?.enemy?.isSpawned)
  const enemyID = "5f4feb04fe6c9531fb739f09" ;
  const enemyDamage = 5;
   const spawnEnemyClick = () => {

      dispatch(spawnEnemy(enemyID));
   }


  let body = {
    userId,
    health,
    enemyDamage
  }

  const beginAttack = () => {
      
      console.log(body)
      dispatch(attack(body));
    
  };


  return (
    <div>
      <Navigation />
      <StatsTable/>
      {isSpawned ?    <button onClick={() =>beginAttack()}>Attack!</button> : null}
      <button onClick={() =>spawnEnemyClick()}>Find an enemy!</button>
    </div>
  );
};

export default Arena;
