import * as types from "../types";

//login user

export const login = ({ email, password, userLanguage }) => (dispatch) => {
  const body = { email, password, userLanguage };

  try {
    axios
      .post("http://localhost:9000/users/login", body)
      .then((res) => {
        dispatch({
          type: types.LOGIN_USER,
          payload: res.data,
        });
      })
      .catch((err) => {
        dispatch(
          returnErrors(err.response.data, err.response.status, "LOGIN_FAIL")
        );
        dispatch({
          type: EnumActionTypes.LOGIN_FAIL,
        });
      });
  } catch {}
};
